insert into oa_file(file_origin_name, file_encryption, create_time, update_time, logic_del, file_url, file_type, owner_id, owner_name) values
(#{entity.fileOriginName}, #{entity.fileEncryption}, #{entity.createTime}, #{entity.updateTime}, #{entity.logicDel}, #{entity.fileUrl}, #{entity.fileType}, #{entity.ownerId}, #{entity.ownerName})
on duplicate key update file_origin_name = values(file_origin_name), file_encryption = values(file_encryption), create_time = values(create_time), update_time = values(update_time), logic_del = values(logic_del), file_url = values(file_url), file_type = values(file_type), owner_id = values(owner_id), owner_name = values(owner_name)
